mapper.select:
INSERT INTO TB_CENTER (
	center_name
	, center_manager
	, center_image
	)
VALUES(
	#{centerName }
	, #{centerManager }
	, #{centerImage }
	)

SELECT tc.center_seq , tc.center_name , tc.center_manager , tm.member_name , tc.center_image , (SELECT COUNT(1) FROM TB_MEMBER WHERE member_position_cd = tc.center_seq) AS member_cnt , (SELECT COUNT(1) FROM TB_CHILDREN WHERE children_position_cd = tc.center_seq) AS children_cnt FROM TB_CENTER tc LEFT OUTER JOIN TB_MEMBER tm ON tc.center_manager = tm.member_seq WHERE 1=1
AND center_seq = #{centerSeq }
ORDER BY center_seq

UPDATE TB_CENTER 
   SET center_name = #{centerName }
     , center_manager = #{centerManager }
     , center_image = #{centerImage }
 WHERE center_seq = #{centerSeq }

SELECT tc.children_seq /* 아동 번호 */ , tc.children_name /* 아동 이름 */ , tc.children_birth /* 아동 생년월일 */ , tc.children_diagnosis /* 아동 진단명 */ , tc.children_memo /* 아동 메모 */ , tc.children_position_cd /* 아동 소속 코드 */ , tc.children_group_cd /* 아동 그룹 코드 */ , IFNULL(tcg.group_name, '') AS group_name /* 아동 그룹 이름 */ , tc.children_active_yn /* 아동 활성화 여부 */ , tc.children_prog_st_dt /* 아동 프로그램 시작 일시 */ , tc.children_prog_ed_dt /* 아동 프로그램 종료 일시 */ , tc.children_parents /* 아동 부모님 */ , tc.children_book_img /* 아동 책 이미지 */ FROM TB_CHILDREN tc LEFT OUTER JOIN TB_CHD_GROUP tcg ON tc.children_group_cd = tcg.group_seq WHERE tc.children_position_cd = #{childrenPositionCd } AND tc.children_active_yn = 'Y'
AND tc.children_group_cd IN (${chdGroupSeqArray })
AND tc.children_seq = #{childrenSeq }

SELECT * 
  FROM TB_MEMBER
 WHERE 1=1

   AND member_position_cd = #{memberPositionCd }


   AND member_approval_yn = #{memberApprovalYn }

 ORDER BY member_name

UPDATE TB_MEMBER
   SET member_name = #{memberName }
     , member_email = #{memberEmail }
     , member_cp = #{memberCp }

     , member_pw = #{memberPw }

 WHERE member_seq = #{memberSeq }

INSERT INTO TB_ACCESS_RECORD (
	member_seq, 
	center_seq
)
VALUES (
	#{memberSeq },
	#{centerSeq }
)
ON DUPLICATE KEY UPDATE 
	center_seq = #{centerSeq }
