mapper.select:
SELECT domain_seq , children_seq , domain_sort_order , domain_name , domain_contents , domain_status_cd , domain_reg_dt , domain_status_ch_dt , domain_tmp_seq FROM TB_DOMAIN WHERE children_seq = #{childrenSeq }
AND domain_seq = #{domainSeq }
ORDER BY domain_sort_order

SELECT lto_seq , domain_seq , lto_sort_order , lto_name , lto_contents , lto_status_cd , lto_reg_dt , lto_status_ch_dt , lto_tmp_seq FROM TB_LTO WHERE 1=1
AND domain_seq = #{domainSeq }
AND lto_seq = #{ltoSeq }
ORDER BY lto_sort_order

SELECT sto_seq , lto_seq , sto_sort_order , sto_name , sto_contents , sto_trial_cnt , sto_arr_std_pst , sto_urg_contents , sto_rnfc_contents , sto_memo_contents , sto_status_cd , sto_reg_dt , sto_status_ch_dt , sto_tmp_seq FROM TB_STO WHERE 1=1
AND lto_seq = #{ltoSeq }
AND sto_seq = #{stoSeq }
ORDER BY sto_sort_order

UPDATE ${targetTable }
   SET ${targetColumn }_sort_order = #{oldSortOrder }
 WHERE ${targetColumn }_seq != #{seq }
   AND ${targetColumn }_sort_order = #{newSortOrder }

   AND center_seq = (SELECT center_seq FROM ${targetTable } WHERE ${targetColumn }_seq = #{seq })


   AND domain_seq = (SELECT domain_seq FROM ${targetTable } WHERE ${targetColumn }_seq = #{seq })


   AND lto_seq = (SELECT lto_seq FROM ${targetTable } WHERE ${targetColumn }_seq = #{seq })

INSERT INTO TB_DOMAIN (
	children_seq,
	domain_sort_order,
	domain_name,
	domain_contents,
	domain_status_cd,
	domain_reg_dt,
	domain_status_ch_dt,
	domain_tmp_seq
	)
SELECT #{childrenSeq } 
	 , IFNULL(MAX(domain_sort_order), 0) + 1 AS domain_sort_order
	 , #{domainName }
	 , #{domainContents }
	 , 'WIT'
	 , current_timestamp()
	 , NULL 
	 , #{domainTmpSeq }
  FROM TB_DOMAIN
 WHERE children_seq = #{childrenSeq }

UPDATE TB_DOMAIN
   SET domain_name = #{domainName }
     , domain_contents = #{domainContents }

     , domain_status_cd = #{domainStatusCd }
     , domain_status_ch_dt = current_timestamp()

 WHERE domain_seq = #{domainSeq }

INSERT INTO TB_LTO (
	domain_seq,
	lto_sort_order,
	lto_name,
	lto_contents,
	lto_status_cd,
	lto_reg_dt,
	lto_status_ch_dt,
	lto_tmp_seq
	)
SELECT #{domainSeq }
	 , IFNULL(MAX(lto_sort_order), 0) + 1 AS lto_sort_order
	 , #{ltoName }
	 , #{ltoContents }
	 , 'WIT'
	 , current_timestamp()
	 , NULL
	 , #{ltoTmpSeq }
  FROM TB_LTO
 WHERE domain_seq = #{domainSeq }

UPDATE TB_LTO
   SET lto_name = #{ltoName }
     , lto_contents = #{ltoContents }

     , lto_status_cd = #{ltoStatusCd }
     , lto_status_ch_dt = current_timestamp()

 WHERE lto_seq = #{ltoSeq }

INSERT INTO TB_STO (
	lto_seq,
	sto_sort_order,
	sto_name,
	sto_contents,
	sto_trial_cnt,
	sto_arr_std_pst,
	sto_urg_contents,
	sto_rnfc_contents,
	sto_memo_contents,
	sto_status_cd,
	sto_reg_dt,
	sto_status_ch_dt,
	sto_tmp_seq,
	sto_round
	)
SELECT #{ltoSeq }
	 , IFNULL(MAX(sto_sort_order), 0) + 1 AS sto_sort_order
	 , #{stoName }
	 , #{stoContents }
	 , #{stoTrialCnt }
	 , #{stoArrStdPst }
	 , #{stoUrgContents }
	 , #{stoRnfcContents }
	 , #{stoMemoContents }
	 , 'WIT'
	 , current_timestamp()
	 , #{stoStatusChDt }
	 , 0
	 , 1
  FROM TB_STO
 WHERE lto_seq = #{ltoSeq }

UPDATE TB_STO
   SET sto_name = #{stoName }
     , sto_contents = #{stoContents }
     , sto_trial_cnt = #{stoTrialCnt }
     , sto_arr_std_pst = #{stoArrStdPst }
     , sto_urg_contents = #{stoUrgContents }
     , sto_rnfc_contents = #{stoRnfcContents }
     , sto_memo_contents = #{stoMemoContents }

     , sto_status_cd = #{stoStatusCd }
     , sto_status_ch_dt = current_timestamp()

 WHERE sto_seq = #{stoSeq }
