mapper.select:
SELECT td.domain_seq AS domain_seq , td.domain_name AS domain_name
, SUM(CASE WHEN date_format(tl.lto_status_ch_dt, '%Y-%m-%d') <= #{dtm } THEN 1 ELSE 0 END) AS #{dtm }
FROM TB_DOMAIN td LEFT OUTER JOIN ( SELECT * FROM TB_LTO WHERE lto_status_cd IN ('CMP', 'RPT') ) tl ON td.domain_seq = tl.domain_seq WHERE td.children_seq = #{childrenSeq } GROUP BY td.domain_seq ORDER BY td.domain_sort_order
