mapper.select:
UPDATE TB_STO 
   SET sto_status_cd = #{stoStatusCd }
     , sto_status_ch_dt = current_timestamp()
 WHERE sto_seq = #{stoSeq }
   AND sto_status_cd != 'CMP'

SELECT sto_round
  FROM TB_STO
 WHERE sto_seq = #{stoSeq }

UPDATE TB_STO
   SET sto_round = sto_round + 1
 WHERE sto_seq = #{stoSeq }
