mapper.select:
UPDATE TB_BOARD
   SET board_contents = #{boardContents }
 WHERE board_reg_mmr_seq = #{memberSeq }
   AND board_dv_cd = 'MMM'

SELECT calendar_seq , calendar_dt , calendar_contents , calendar_color_cd , calendar_reg_mmr_seq , calendar_reg_dt , center_seq FROM TB_CALENDAR WHERE center_seq = #{centerSeq } AND calendar_dt = #{calendarDt }
AND calendar_seq = #{calendarSeq }
ORDER BY calendar_seq

UPDATE TB_CALENDAR
   SET calendar_dt = #{calendarDt }
     , calendar_contents = #{calendarContents }
     , calendar_color_cd = #{calendarColorCd }
 WHERE calendar_seq = #{calendarSeq }

SELECT tc.children_seq , tc.children_name , tc.children_book_img , ts.scheduler_seq , ts.scheduler_week_cd , ts.scheduler_time_cd , ts.scheduler_reg_mmr_seq , ts.scheduler_reg_dt FROM TB_CHILDREN tc LEFT OUTER JOIN TB_SCHEDULER ts ON tc.children_seq = ts.children_seq WHERE tc.children_position_cd = #{centerSeq } AND tc.children_active_yn = 'Y' AND ts.scheduler_seq IS NOT NULL
AND tc.children_seq = #{childrenSeq }
ORDER BY ts.scheduler_time_cd, FIELD(ts.scheduler_week_cd, 'MON', 'TUE', 'WED', 'THU', 'FRI'), tc.children_seq
