mapper.select:
 		SELECT group_seq
			 , group_name
			 , group_posiotion_cd
		  FROM TB_CHD_GROUP
		 WHERE group_name = #{groupName }
		   AND group_posiotion_cd = #{groupPosiotionCd }

 		INSERT INTO TB_CHD_GROUP (
			group_name,
			group_posiotion_cd
			)
		VALUES(
			#{groupName },
			#{groupPosiotionCd }
			)

SELECT CASE WHEN (COUNT(*) % 10) = 0 THEN TRUNCATE(COUNT(*) / 10, 0) ELSE TRUNCATE(COUNT(*) / 10, 0) + 1 END AS page_cnt FROM TB_MEMBER tm WHERE 1=1
AND tm.member_name LIKE CONCAT('%',#{memberName },'%')
AND tm.member_position_cd = #{memberPositionCd }

SELECT CASE WHEN (COUNT(*) % 10) = 0 THEN TRUNCATE(COUNT(*) / 10, 0) ELSE TRUNCATE(COUNT(*) / 10, 0) + 1 END AS page_cnt FROM TB_CHILDREN tc WHERE 1=1
AND tc.children_name LIKE CONCAT('%',#{childrenName },'%')
AND tc.children_position_cd = #{childrenPositionCd }

UPDATE TB_MEMBER 
   SET member_pw = 'neulpum1234!'
 WHERE member_seq = #{memberSeq }

mapper.delete:
DELETE FROM TB_MEMBER
 WHERE member_seq = #{memberSeq }
   AND member_auth_cd != 'master'
